import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angularapp';
  opt1: number = 0; // Initialize with a default value
  opt2: number = 0; // Initialize with a default value
  amt: number = 0;  // Initialize with a default value
  res: number = 0;  // Initialize with a default value
  result: string = ''; // Initialize with an empty string
  exchange(a: number, b: number, amt: number) {
    if (a != null && b != null) {
      this.result = (b / a * amt).toFixed(2);
      return this.result;
    }
    return 0; // Return a default value if the condition is not met
  }
}
